                    <p className="text-sm sm:text-base font-bold tabular-nums" data-testid="text-odds">
                      {bookmaker.odds.toFixed(2)}
                    </p>
                  </div>

                  <ArrowRight className="hidden sm:block h-4 w-4 text-muted-foreground" aria-hidden="true" />

                  <div className="text-left sm:text-right">
                    <p className="text-[10px] sm:text-xs text-muted-foreground uppercase tracking-wider font-bold">Stake</p>
                    <p className="text-sm sm:text-base font-bold tabular-nums" data-testid="text-stake">
                      ${bookmaker.stake.toFixed(2)}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Action Indicator */}
        <div className="flex items-center justify-center pt-1 sm:pt-2">
          <Button
            variant="ghost"
            size="sm"
            className="gap-2 text-xs sm:text-sm font-semibold group/btn w-full sm:w-auto"
            onClick={(e) => {
              e.stopPropagation();
              onClick?.();
            }}
          >
            View Full Details
            <ArrowRight className="h-3 w-3 sm:h-4 sm:w-4 transition-transform group-hover/btn:translate-x-1" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/DashboardHeader.ts.content:
import { useEffect } from "react";
import { RefreshCw, Settings, TrendingUp, Sparkles } from "lucide-react";
import { Button } from "@/components/ui/button";
import StatusIndicator from "./StatusIndicator";
import ThemeToggle from "./ThemeToggle";
import { cn } from "@/lib/utils";

interface DashboardHeaderProps {
  status: "connected" | "disconnected" | "cached";
  onRefresh: () => void;
  onSettingsClick: () => void;
  isRefreshing?: boolean;
}

export default function DashboardHeader({
  status,
  onRefresh,
  onSettingsClick,
  isRefreshing = false,
}: DashboardHeaderProps) {
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'r') {
        e.preventDefault();
        onRefresh();
      } else if ((e.metaKey || e.ctrlKey) && e.key === ',') {
        e.preventDefault();
        onSettingsClick();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onRefresh, onSettingsClick]);

  return (
    <header 
      className={cn(
        "sticky top-0 z-50 border-b border-border/50",
        "bg-background/80 backdrop-blur-xl supports-[backdrop-filter]:bg-background/60",
        "shadow-sm"
      )}
      role="banner"
    >
      {/* Gradient overlay */}
      <div className="absolute inset-0 bg-gradient-to-r from-primary/5 via-transparent to-primary/5 pointer-events-none" aria-hidden="true" />

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 relative">
        <div className="flex h-16 items-center justify-between gap-4">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-4">
              <div className={cn(
                "relative bg-gradient-to-br from-primary to-primary/80 p-2 rounded-lg",
                "shadow-lg shadow-primary/20"
              )}>
                <TrendingUp className="h-5 w-5 text-primary-foreground" aria-hidden="true" />
                <Sparkles className="absolute -top-0.5 -right-0.5 h-3 w-3 text-primary-foreground animate-pulse" aria-hidden="true" />
              </div>
              <div className="flex flex-col">
                <h1 className="text-xl sm:text-2xl font-black tracking-tight bg-gradient-to-r from-foreground to-foreground/70 bg-clip-text text-transparent" data-testid="text-app-title">
                  Arbitrage Scanner
                </h1>
                <p className="text-xs text-muted-foreground font-medium hidden sm:block">
                  Find guaranteed profit opportunities
                </p>
              </div>
            </div>
            <StatusIndicator status={status} className="hidden lg:flex" />
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="default"
              onClick={onRefresh}
              disabled={isRefreshing}
              data-testid="button-refresh"
              aria-label={isRefreshing ? "Refreshing opportunities" : "Refresh opportunities (Cmd/Ctrl+R)"}
              title={isRefreshing ? "Refreshing..." : "Refresh (Cmd/Ctrl+R)"}
              className={cn(
                "gap-2 font-semibold",
                "hover:bg-primary/10 dark:hover:bg-primary/20 transition-colors duration-200"
              )}
            >
              <RefreshCw className={cn("h-4 w-4 transition-transform duration-500", isRefreshing && "animate-spin")} />
              <span className="hidden sm:inline">Refresh</span>
            </Button>
            <Button
              variant="ghost"
              size="icon"
              onClick={onSettingsClick}
              data-testid="button-settings"
              aria-label="Open settings (Cmd/Ctrl+,)"
              title="Settings (Cmd/Ctrl+,)"
              className="hover:bg-primary/10 dark:hover:bg-primary/20"
            >
              <Settings className="h-5 w-5" />
            </Button>
            <ThemeToggle />
          </div>
        </div>
      </div>
    </header>
  );
}

client/src/components/FilterBar.tsx.content:
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  X, 
  Trophy, 
  Circle, 
  CircleDot,
  Shield,
  Snowflake,
  Filter,
  TrendingUp,
  Sparkles,
  Dumbbell
} from "lucide-react";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Card } from "@/components/ui/card";
import { cn } from "@/lib/utils";

const SPORTS = [
  { value: "all", label: "All Sports", Icon: Trophy },
  { value: "soccer", label: "Soccer", Icon: Circle },
  { value: "basketball", label: "Basketball", Icon: CircleDot },
  { value: "football", label: "Football", Icon: Shield },
  { value: "baseball", label: "Baseball", Icon: Circle },
  { value: "hockey", label: "Hockey", Icon: Snowflake },
  { value: "mma", label: "MMA", Icon: Dumbbell },
];

export interface BookmakerWithCount {
  name: string;
  count: number;
}

interface FilterBarProps {
  selectedSport: string;
  selectedBookmakers: string[];
  minProfit: number;
  availableBookmakers: BookmakerWithCount[];
  onSportChange: (sport: string) => void;
  onBookmakerToggle: (bookmaker: string) => void;
  onMinProfitChange: (profit: number) => void;
  onClearFilters: () => void;
}

export default function FilterBar({
  selectedSport,
  selectedBookmakers,
  minProfit,
  availableBookmakers = [],
  onSportChange,
  onBookmakerToggle,
  onMinProfitChange,
  onClearFilters,
}: FilterBarProps) {
  const hasFilters = selectedSport !== "all" || selectedBookmakers.length > 0 || minProfit > 0;

  return (
    <div className="border-b border-border/50 bg-muted/30 dark:bg-muted/10" role="region" aria-label="Filters">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4">
        <Card className={cn(
          "p-5 space-y-5 border-border/50",
          "bg-card/50 backdrop-blur-sm"
        )}>
          <div className="flex items-center justify-between flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <div className={cn(
                "bg-gradient-to-br from-primary/20 to-primary/10 p-2 rounded-lg",
                "ring-1 ring-primary/20"
              )}>
                <Filter className="h-4 w-4 text-primary" aria-hidden="true" />
              </div>
              <div>
                <h2 className="text-base font-bold flex items-center gap-2">
                  Filter Opportunities
                  {hasFilters && <Sparkles className="h-3.5 w-3.5 text-primary animate-pulse" aria-hidden="true" />}
                </h2>
                <p className="text-xs text-muted-foreground font-medium">
                  Refine your search to find the best opportunities
                </p>
              </div>
            </div>
            {hasFilters && (
              <Button
                variant="outline"
                size="default"
                onClick={onClearFilters}
                data-testid="button-clear-filters"
                aria-label="Clear all filters"
                className={cn(
                  "gap-2 font-semibold",
                  "hover:bg-destructive/10 hover:text-destructive hover:border-destructive/30 transition-all duration-200"
                )}
              >
                <X className="h-4 w-4" aria-hidden="true" />
                Clear All
              </Button>
            )}
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-5">
            {/* Sport Category */}
            <div className="space-y-2">
              <label className="text-xs font-bold text-foreground flex items-center gap-2 uppercase tracking-wide">
                <Trophy className="h-4 w-4 text-primary" aria-hidden="true" />
                Sport Category
              </label>
              <Select value={selectedSport} onValueChange={onSportChange}>
                <SelectTrigger 
                  className={cn(
                    "w-full font-semibold",
                    "hover:bg-muted/50 transition-colors duration-200"
                  )} 
                  data-testid="select-sport" 
                  aria-label="Select sport to filter"
                >
                  <SelectValue>
                    {SPORTS.find(s => s.value === selectedSport)?.label || "All Sports"}
                  </SelectValue>
                </SelectTrigger>
                <SelectContent>
                  {SPORTS.map((sport) => (
                    <SelectItem key={sport.value} value={sport.value}>
                      <div className="flex items-center gap-3">
                        <sport.Icon className="h-4 w-4" aria-hidden="true" />
                        <span className="font-medium">{sport.label}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Minimum Profit */}
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <label htmlFor="profit-slider" className="text-xs font-bold text-foreground flex items-center gap-2 uppercase tracking-wide">
                  <TrendingUp className="h-4 w-4 text-primary" aria-hidden="true" />
                  Min Profit
                </label>
                <div className={cn(
                  "px-3 py-1.5 rounded-lg font-black font-mono tabular-nums text-lg",
                  "bg-primary/10 text-primary ring-1 ring-primary/20"
                )} 
                data-testid="text-profit-value" 
                aria-live="polite">
                  {minProfit.toFixed(1)}%
                </div>
              </div>
              <div className="pt-2 space-y-2">
                <Slider
                  id="profit-slider"
                  value={[minProfit]}
                  onValueChange={([value]) => onMinProfitChange(value)}
                  max={10}
                  step={0.1}
                  className="w-full"
                  data-testid="slider-min-profit"
                  aria-label={`Minimum profit filter, currently ${minProfit.toFixed(1)}%`}
                />
                <div className="flex justify-between text-xs text-muted-foreground font-semibold">
                  <span>0%</span>
                  <span>5%</span>
                  <span>10%</span>
                </div>
              </div>
            </div>

            {/* Bookmakers Info */}
            <div className="space-y-2">
              <label className="text-xs font-bold text-foreground flex items-center gap-2 uppercase tracking-wide">
                <CircleDot className="h-4 w-4 text-primary" aria-hidden="true" />
                Bookmakers
              </label>
              <div className={cn(
                "flex items-center justify-center h-11 px-4 rounded-lg",
                "bg-muted/50 border border-border/50"
              )}>
                <span className="text-xs font-semibold text-muted-foreground">
                  {selectedBookmakers.length > 0 
                    ? `${selectedBookmakers.length} of ${availableBookmakers.length} selected` 
                    : `All ${availableBookmakers.length} available`}
                </span>
              </div>
            </div>
          </div>

          {/* Bookmakers Grid */}
          {availableBookmakers.length > 0 && (
            <div className="space-y-2 pt-1">
              <div className="flex flex-wrap gap-2" role="group" aria-label="Bookmaker filters">
                {availableBookmakers.map((bookmaker) => {
                  const isSelected = selectedBookmakers.includes(bookmaker.name);
                  return (
                    <Badge
                      key={bookmaker.name}
                      variant={isSelected ? "default" : "outline"}
                      className={cn(
                        "cursor-pointer transition-all duration-200 px-3 py-1.5 text-xs font-bold",
                        isSelected 
                          ? "shadow-lg shadow-primary/20 ring-1 ring-primary/30" 
                          : "hover-elevate hover:border-primary/30"
                      )}
                      onClick={() => onBookmakerToggle(bookmaker.name)}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' || e.key === ' ') {
                          e.preventDefault();
                          onBookmakerToggle(bookmaker.name);
                        }
                      }}
                      tabIndex={0}
                      role="checkbox"
                      aria-checked={isSelected}
                      aria-label={`Filter by ${bookmaker.name}, ${bookmaker.count} opportunities`}
                      data-testid={`badge-bookmaker-${bookmaker.name.toLowerCase().replace(/\s+/g, '-')}`}
                    >
                      {bookmaker.name}
                      <span className={cn(
                        "ml-2 px-1.5 py-0.5 rounded text-xs font-black",
                        isSelected ? "bg-primary-foreground/20" : "bg-muted"
                      )}>
                        {bookmaker.count}
                      </span>
                    </Badge>
                  );
                })}
              </div>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

client/src/components/SettingsDialog.tsx.content:
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Eye, EyeOff } from "lucide-react";
import { queryClient } from "@/lib/queryClient";

interface SettingsDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function SettingsDialog({ open, onOpenChange }: SettingsDialogProps) {
  const [apiKey, setApiKey] = useState(() => localStorage.getItem("oddsApiKey") || "");
  const [showApiKey, setShowApiKey] = useState(false);
  const [mockMode, setMockMode] = useState(() => localStorage.getItem("mockMode") === "true");
  const [isSaving, setIsSaving] = useState(false);
  const [saveError, setSaveError] = useState<string | null>(null);

  const handleApiKeyChange = (value: string) => {
    setApiKey(value);
    setSaveError(null);
    if (value.trim().length > 0 && mockMode) {
      setMockMode(false);
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    setSaveError(null);

    try {
      const response = await fetch("/api/settings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ mockMode }),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      localStorage.setItem("oddsApiKey", apiKey);
      localStorage.setItem("mockMode", mockMode.toString());

      // Invalidate all odds queries to force immediate refetch with new settings
      // Use predicate to match any query key that starts with /api/odds
      await queryClient.invalidateQueries({ 
        predicate: (query) => {
          const key = query.queryKey[0];
          return typeof key === 'string' && key.startsWith('/api/odds');
        }
      });

      setIsSaving(false);
      onOpenChange(false);
    } catch (error) {
      console.error("Failed to save settings:", error);
      setSaveError("Failed to save. Please try again.");
      setIsSaving(false);
    }
  };

  const handleCancel = () => {
    setApiKey(localStorage.getItem("oddsApiKey") || "");
    setMockMode(localStorage.getItem("mockMode") === "true");
    setSaveError(null);
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]" data-testid="dialog-settings" aria-describedby="dialog-description">
        <DialogHeader>
          <DialogTitle>Settings</DialogTitle>
          <DialogDescription id="dialog-description">
            Configure API and data preferences
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 py-4">
          <div className="space-y-2">
            <Label htmlFor="api-key">Odds API Key</Label>
            <div className="relative">
              <Input
                id="api-key"
                type={showApiKey ? "text" : "password"}
                placeholder="Enter your API key"
                value={apiKey}
                onChange={(e) => handleApiKeyChange(e.target.value)}
                className="pr-12 !h-11"
                data-testid="input-api-key"
                aria-label="API key"
                aria-invalid={!!saveError}
                autoComplete="off"
              />
              <Button
                type="button"
                variant="ghost"
                className="absolute right-0 top-0 !h-11 !w-11 !p-0 hover:bg-transparent"
                onClick={() => setShowApiKey(!showApiKey)}
                data-testid="button-toggle-api-key"
                aria-label={showApiKey ? "Hide API key" : "Show API key"}
              >
                {showApiKey ? (
                  <EyeOff className="h-4 w-4" aria-hidden="true" />
                ) : (
                  <Eye className="h-4 w-4" aria-hidden="true" />
                )}
              </Button>
            </div>
            <p className="text-xs text-muted-foreground">
              Get your key from{" "}
              <a
                href="https://the-odds-api.com"
                target="_blank"
                rel="noopener noreferrer"
                className="text-primary hover:underline focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 rounded"
              >
                the-odds-api.com
              </a>
            </p>
          </div>

          <div className="flex items-center justify-between gap-4">
            <div className="space-y-0.5 flex-1">
              <Label htmlFor="mock-mode">Mock Data Mode</Label>
              <p className="text-xs text-muted-foreground">
                {mockMode 
                  ? "Using simulated data"
                  : apiKey.trim()
                    ? "Using live data"
                    : "Add API key for live data"}
              </p>
            </div>
            <Switch
              id="mock-mode"
              checked={mockMode}
              onCheckedChange={setMockMode}
              data-testid="switch-mock-mode"
              aria-label="Toggle mock data mode"
            />
          </div>

          {saveError && (
            <div className="p-3 rounded-md bg-destructive/10 border border-destructive/20" role="alert">
              <p className="text-sm text-destructive">{saveError}</p>
            </div>
          )}
        </div>

        <DialogFooter className="gap-2">
          <Button 
            variant="outline" 
            onClick={handleCancel} 
            data-testid="button-cancel"
            disabled={isSaving}
            className="h-11 px-6"
          >
            Cancel
          </Button>
          <Button 
            onClick={handleSave} 
            data-testid="button-save"
            disabled={isSaving}
            aria-label={isSaving ? "Saving settings" : "Save settings"}
            className="h-11 px-6"
          >
            {isSaving ? "Saving..." : "Save Changes"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/StatusIndicator.ts.content:
import { cn } from "@/lib/utils";
import { Wifi, WifiOff, Database } from "lucide-react";
import { Badge } from "@/components/ui/badge";

type StatusType = "connected" | "disconnected" | "cached";

interface StatusIndicatorProps {
  status: StatusType;
  className?: string;
}

export default function StatusIndicator({ status, className }: StatusIndicatorProps) {
  const statusConfig = {
    connected: {
      color: "bg-emerald-500 dark:bg-emerald-400",
      textColor: "text-emerald-700 dark:text-emerald-400",
      bgColor: "bg-emerald-500/10 dark:bg-emerald-500/20",
      borderColor: "border-emerald-500/30",
      label: "Live",
      description: "Real-time data",
      Icon: Wifi,
      pulse: true,
      ariaLabel: "Connected to live data",
    },
    disconnected: {
      color: "bg-red-500 dark:bg-red-400",
      textColor: "text-red-700 dark:text-red-400",
      bgColor: "bg-red-500/10 dark:bg-red-500/20",
      borderColor: "border-red-500/30",
      label: "Offline",
      description: "No connection",
      Icon: WifiOff,
      pulse: false,
      ariaLabel: "Disconnected from data source",
    },
    cached: {
      color: "bg-amber-500 dark:bg-amber-400",
      textColor: "text-amber-700 dark:text-amber-400",
      bgColor: "bg-amber-500/10 dark:bg-amber-500/20",
      borderColor: "border-amber-500/30",
      label: "Cached",
      description: "Stored data",
      Icon: Database,
      pulse: false,
      ariaLabel: "Showing cached data",
    },
  };

  const config = statusConfig[status];
  const StatusIcon = config.Icon;

  return (
    <Badge
      variant="outline"
      className={cn(
        "px-3 py-1.5 gap-2 border-2",
        config.bgColor,
        config.borderColor,
        className
      )}
      data-testid={`status-${status}`}
      role="status"
      aria-label={config.ariaLabel}
    >
      <div className="relative flex items-center" aria-hidden="true">
        <StatusIcon className={cn("h-4 w-4", config.textColor)} />
        {config.pulse && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className={cn("h-4 w-4 rounded-full animate-ping opacity-50", config.color)} />
          </div>
        )}
      </div>
      <div className="flex flex-col items-start">
        <span className={cn("text-sm font-semibold leading-none", config.textColor)}>
          {config.label}
        </span>
        <span className="text-xs text-muted-foreground leading-none mt-0.5">
          {config.description}
        </span>
      </div>
    </Badge>
  );
}

