      // Fair price for Team A is average of [2.00, 2.05, 2.20] = 48.97% implied prob
      // FanDuel offers 2.20 (45.45% implied prob)
      // EV = ((0.4897 - 0.4545) / 0.4545) * 100 ≈ 7.74%
      expect(fanDuelTeamABet!.ev).toBeCloseTo(7.74, 0);
    });
  });
