import type { OddsApiEvent, Sport } from "@shared/schema";
import { oddsApiEventSchema } from "@shared/schema";
import { z } from "zod";

// ============================================================================
// ODDS PROVIDER INTERFACE & IMPLEMENTATIONS
// ============================================================================

export interface OddsResult {
  events: OddsApiEvent[];
  isFromCache: boolean;
  cacheAge?: number; // Age in minutes
}

export interface OddsProvider {
  fetchOdds(sports: Sport[], regions?: string[], markets?: string[]): Promise<OddsResult>;
  getName(): string;
}

// ============================================================================
// IN-MEMORY CACHE WITH TTL
// ============================================================================

interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
}

interface PersistentCacheEntry<T> {
  data: T;
  timestamp: number;
  isFallback: boolean;
}

class InMemoryCache {
  private cache: Map<string, CacheEntry<any>> = new Map();
  private persistentCache: Map<string, PersistentCacheEntry<any>> = new Map();

  set<T>(key: string, data: T, ttlSeconds: number = 60): void {
    this.cache.set(key, {
      data,
      timestamp: Date.now(),
      ttl: ttlSeconds * 1000,
    });

    // Also save to persistent cache for fallback
    this.persistentCache.set(key, {
      data,
      timestamp: Date.now(),
      isFallback: false,
    });
  }

  get<T>(key: string): T | null {
    const entry = this.cache.get(key);

    if (!entry) return null;

    const isExpired = Date.now() - entry.timestamp > entry.ttl;

    if (isExpired) {
      this.cache.delete(key);
      return null;
    }

    return entry.data as T;
  }

  getFallback<T>(key: string): { data: T; timestamp: number } | null {
    const entry = this.persistentCache.get(key);
    if (!entry) return null;

    return {
      data: entry.data as T,
      timestamp: entry.timestamp,
    };
  }

  has(key: string): boolean {
    return this.get(key) !== null;
  }

  hasFallback(key: string): boolean {
    return this.persistentCache.has(key);
  }

  clear(): void {
    this.cache.clear();
  }

  clearAll(): void {
    this.cache.clear();
    this.persistentCache.clear();
  }

  getStats() {
    let totalEntries = 0;
    let expiredEntries = 0;

    for (const [key, entry] of Array.from(this.cache.entries())) {
      totalEntries++;
      if (Date.now() - entry.timestamp > entry.ttl) {
        expiredEntries++;
      }
    }

    return {
      totalEntries,
      activeEntries: totalEntries - expiredEntries,
      expiredEntries,
      persistentEntries: this.persistentCache.size,
    };
  }
}

export const oddsCache = new InMemoryCache();

// ============================================================================
// THE ODDS API PROVIDER
// ============================================================================

export class TheOddsApiProvider implements OddsProvider {
  private apiKey: string;
  private baseUrl: string = "https://api.the-odds-api.com/v4";
  private cacheTtl: number = 60; // seconds

  constructor(apiKey: string, cacheTtl: number = 60) {
    this.apiKey = apiKey;
    this.cacheTtl = cacheTtl;
  }

  getName(): string {
    return "The Odds API";
  }

  private getCacheKey(sports: Sport[], regions: string[], markets: string[]): string {
    return `odds:${sports.join(',')}:${regions.join(',')}:${markets.join(',')}`;
  }

  async fetchOdds(
    sports: Sport[],
    regions: string[] = ["us", "uk", "eu"],
    markets: string[] = ["h2h"]
  ): Promise<OddsResult> {
    const cacheKey = this.getCacheKey(sports, regions, markets);

    // Check cache first
    const cached = oddsCache.get<OddsApiEvent[]>(cacheKey);
    if (cached) {
      console.log(`[OddsAPI] Cache hit for key: ${cacheKey}`);
      return {
        events: cached,
        isFromCache: false, // This is fresh cache, not fallback
        cacheAge: 0,
      };
    }

    console.log(`[OddsAPI] Fetching fresh data for sports: ${sports.join(', ')}`);

    try {
      // Fetch odds for each sport in parallel
      const promises = sports.map(sport => this.fetchSportOdds(sport, regions, markets));
      const results = await Promise.all(promises);

      // Flatten results
      const allEvents = results.flat();

      // Validate with Zod
      const validatedEvents = z.array(oddsApiEventSchema).parse(allEvents);

      // Cache the results (both TTL and persistent)
      oddsCache.set(cacheKey, validatedEvents, this.cacheTtl);

      console.log(`[OddsAPI] Fetched ${validatedEvents.length} events, cached for ${this.cacheTtl}s`);

      return {
        events: validatedEvents,
        isFromCache: false,
        cacheAge: 0,
      };
    } catch (error) {
      console.error("[OddsAPI] Error fetching odds:", error);

      // Try to fall back to cached data
      const fallback = oddsCache.getFallback<OddsApiEvent[]>(cacheKey);
      if (fallback) {
        const ageMinutes = Math.floor((Date.now() - fallback.timestamp) / 60000);
        console.log(`[OddsAPI] Using cached fallback data (${ageMinutes}m old)`);
        return {
          events: fallback.data,
          isFromCache: true,
          cacheAge: ageMinutes,
        };
      }

      throw new Error(`Failed to fetch odds: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private async fetchSportOdds(
    sport: Sport,
    regions: string[],
    markets: string[]
  ): Promise<OddsApiEvent[]> {
    const params = new URLSearchParams({
      apiKey: this.apiKey,
      regions: regions.join(','),
      markets: markets.join(','),
      oddsFormat: 'decimal',
    });

    const url = `${this.baseUrl}/sports/${sport}/odds?${params.toString()}`;

    const response = await fetch(url);

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }

    const data = await response.json();
    return Array.isArray(data) ? data : [];
  }
}

