export function AppSidebar({
  selectedSport,
  selectedBookmakers,
  minProfit,
  availableBookmakers = [],
  onSportChange,
  onBookmakerToggle,
  onMinProfitChange,
  onClearFilters,
  onSettingsClick,
}: AppSidebarProps) {
  const hasFilters = selectedSport !== "all" || selectedBookmakers.length > 0 || minProfit > 0;
  const activeFiltersCount = (selectedSport !== "all" ? 1 : 0) + selectedBookmakers.length + (minProfit > 0 ? 1 : 0);

  return (
