          <div className="flex-1">
            <h2 className="text-xl font-bold text-sidebar-foreground tracking-tight" data-testid="text-sidebar-title">
              DELLTA
            </h2>
            <p className="text-sm text-muted-foreground font-medium">Arbitrage Scanner</p>
          </div>
        </div>
      </SidebarHeader>

      <SidebarContent className="px-4 py-6 space-y-8">
        {/* Sport Category Section */}
        <SidebarGroup>
          <div className="flex items-center justify-between px-2 pb-4">
            <SidebarGroupLabel className="text-xs uppercase tracking-widest text-muted-foreground font-bold flex items-center gap-2">
              <Trophy className="h-3.5 w-3.5" />
              Sport Category
            </SidebarGroupLabel>
            {selectedSport !== "all" && (
              <Badge variant="secondary" className="text-[10px] h-5 px-1.5">
                1
              </Badge>
            )}
          </div>
          <SidebarGroupContent>
            <SidebarMenu className="space-y-1">
              {SPORTS.map((sport) => {
                const isActive = selectedSport === sport.value;
                return (
                  <SidebarMenuItem key={sport.value}>
                    <SidebarMenuButton
                      onClick={() => onSportChange(sport.value)}
                      isActive={isActive}
                      data-testid={`button-sport-${sport.value}`}
                      className={cn(
                        "gap-3 h-11 px-3 rounded-lg font-medium transition-all",
                        isActive && "bg-primary/10 text-primary hover:bg-primary/15"
                      )}
                      tooltip={sport.description}
                    >
                      <sport.Icon className={cn(
                        "h-4.5 w-4.5 transition-colors",
                        isActive ? "text-primary" : "text-muted-foreground"
                      )} />
                      <span className="flex-1 text-sm">{sport.label}</span>
                      {isActive && <ChevronRight className="h-4 w-4 text-primary" />}
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                );
              })}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        <Separator />

        {/* Min Profit Section */}
        <SidebarGroup>
          <div className="flex items-center justify-between px-2 pb-4">
            <SidebarGroupLabel className="text-xs uppercase tracking-widest text-muted-foreground font-bold flex items-center gap-2">
              <Percent className="h-3.5 w-3.5" />
              Min Profit
            </SidebarGroupLabel>
            <Badge 
              variant={minProfit > 0 ? "default" : "outline"} 
              className={cn(
                "text-xs h-6 px-2 font-bold tabular-nums",
                minProfit > 0 && "bg-primary/15 text-primary border-primary/30"
              )}
              data-testid="badge-min-profit"
            >
              {minProfit.toFixed(1)}%
            </Badge>
          </div>
          <SidebarGroupContent className="px-2 space-y-4">
            <Slider
              value={[minProfit]}
              onValueChange={(value) => onMinProfitChange(value[0])}
              max={10}
              step={0.1}
              className="w-full"
              data-testid="slider-min-profit"
            />
            <div className="flex justify-between items-center text-xs text-muted-foreground font-medium px-1">
              <span className={cn(minProfit === 0 && "text-primary font-semibold")}>0%</span>
              <span className={cn(minProfit >= 4.5 && minProfit <= 5.5 && "text-primary font-semibold")}>5%</span>
              <span className={cn(minProfit === 10 && "text-primary font-semibold")}>10%</span>
            </div>
          </SidebarGroupContent>
        </SidebarGroup>

        {/* Bookmakers Section */}
        {availableBookmakers.length > 0 && (
          <>
            <Separator />
            <SidebarGroup>
              <div className="flex items-center justify-between px-2 pb-4">
                <SidebarGroupLabel className="text-xs uppercase tracking-widest text-muted-foreground font-bold flex items-center gap-2">
                  <Shield className="h-3.5 w-3.5" />
                  Bookmakers
                </SidebarGroupLabel>
                <div className="flex items-center gap-1.5">
                  {selectedBookmakers.length > 0 && (
                    <Badge variant="secondary" className="text-[10px] h-5 px-1.5">
                      {selectedBookmakers.length}
                    </Badge>
                  )}
                  <Badge variant="outline" className="text-[10px] h-5 px-1.5">
                    {availableBookmakers.reduce((sum, b) => sum + b.count, 0)}
                  </Badge>
                </div>
              </div>
              <SidebarGroupContent className="px-2">
                <div className="flex flex-wrap gap-2">
                  {availableBookmakers.map(({ name, count }) => {
                    const isSelected = selectedBookmakers.includes(name);
                    return (
                      <Badge
                        key={name}
                        variant={isSelected ? "default" : "outline"}
                        className={cn(
                          "cursor-pointer text-xs h-8 px-3 font-medium hover-elevate active-elevate-2 transition-all",
                          isSelected && "bg-primary text-primary-foreground border-primary shadow-sm"
                        )}
                        onClick={() => onBookmakerToggle(name)}
                        data-testid={`badge-bookmaker-${name.toLowerCase()}`}
                      >
                        {name}
                        <span className={cn(
                          "ml-1.5 opacity-70 font-semibold",
                          isSelected && "opacity-90"
                        )}>
                          {count}
                        </span>
                      </Badge>
                    );
                  })}
                </div>
              </SidebarGroupContent>
            </SidebarGroup>
          </>
        )}

        {/* Clear Filters */}
        {hasFilters && (
          <>
            <Separator />
            <SidebarGroup>
              <SidebarGroupContent className="px-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={onClearFilters}
                  className="w-full gap-2 h-10 font-medium"
                  data-testid="button-clear-filters"
                >
                  <X className="h-4 w-4" />
                  Clear {activeFiltersCount} Filter{activeFiltersCount !== 1 ? 's' : ''}
                </Button>
              </SidebarGroupContent>
            </SidebarGroup>
          </>
        )}
      </SidebarContent>

      <SidebarFooter className="border-t border-sidebar-border p-4">
        <Button
          variant="ghost"
          size="sm"
          onClick={onSettingsClick}
          className="w-full gap-3 justify-start h-11 font-medium"
          data-testid="button-sidebar-settings"
        >
          <Settings className="h-4.5 w-4.5" />
          Settings & API Keys
        </Button>
      </SidebarFooter>
    </Sidebar>
  );
}

client/src/components/OpportunitiesTable.content:
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ArrowRight, DollarSign, TrendingUp, Clock, Target } from "lucide-react";
import { cn } from "@/lib/utils";
import type { ArbitrageOpportunity } from "@/components/ArbitrageCard";

interface OpportunitiesTableProps {
  opportunities: ArbitrageOpportunity[];
  onClick?: (opportunity: ArbitrageOpportunity) => void;
}

export function OpportunitiesTable({ opportunities, onClick }: OpportunitiesTableProps) {
  const getProfitLevel = (profit: number) => {
    if (profit >= 3) return "high";
    if (profit >= 1) return "medium";
    return "low";
  };

  const getProfitColor = (profit: number) => {
    const level = getProfitLevel(profit);
    if (level === "high") return "text-emerald-600 dark:text-emerald-400";
    if (level === "medium") return "text-amber-600 dark:text-amber-400";
    return "text-muted-foreground";
  };

  const getProfitBgColor = (profit: number) => {
    const level = getProfitLevel(profit);
    if (level === "high") return "bg-emerald-500/10";
    if (level === "medium") return "bg-amber-500/10";
    return "bg-muted/50";
  };

  const timeAgo = (timestamp: string) => {
    const now = new Date();
    const then = new Date(timestamp);
    const seconds = Math.floor((now.getTime() - then.getTime()) / 1000);

    if (seconds < 60) return `${seconds}s ago`;
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
    return then.toLocaleTimeString();
  };

  return (
    <div className="rounded-lg sm:rounded-xl border-2 border-border bg-card">
      <div className="overflow-x-auto">
        <Table>
          <TableHeader>
            <TableRow className="bg-muted/50 hover:bg-muted/50 border-b-2">
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider h-10 sm:h-12 whitespace-nowrap">
              <div className="flex items-center gap-1 sm:gap-2">
                <Target className="h-3 w-3 sm:h-3.5 sm:w-3.5" />
                <span className="hidden sm:inline">Match</span>
              </div>
            </TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider whitespace-nowrap">Sport</TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider whitespace-nowrap">Bookmakers</TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider text-right whitespace-nowrap">
              <div className="flex items-center justify-end gap-1 sm:gap-2">
                <TrendingUp className="h-3 w-3 sm:h-3.5 sm:w-3.5" />
                Profit
              </div>
            </TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider text-right whitespace-nowrap">
              <div className="flex items-center justify-end gap-1 sm:gap-2">
                <DollarSign className="h-3 w-3 sm:h-3.5 sm:w-3.5" />
                Stake
              </div>
            </TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider text-right whitespace-nowrap">Returns</TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider text-right whitespace-nowrap hidden sm:table-cell">
              <div className="flex items-center justify-end gap-1 sm:gap-2">
                <Clock className="h-3 w-3 sm:h-3.5 sm:w-3.5" />
                Time
              </div>
            </TableHead>
            <TableHead className="font-bold text-[10px] sm:text-xs uppercase tracking-wider text-right whitespace-nowrap">Action</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
          {opportunities.map((opp) => {
            const totalStake = opp.bookmakers.reduce((sum, b) => sum + b.stake, 0);
            const guaranteedProfit = (totalStake * opp.profit) / 100;

            return (
              <TableRow 
                key={opp.id} 
                className="hover-elevate cursor-pointer transition-all h-14 sm:h-16 border-b"
                onClick={() => onClick?.(opp)}
                data-testid={`row-opportunity-${opp.id}`}
              >
                <TableCell className="font-bold text-xs sm:text-sm max-w-[120px] sm:max-w-none" data-testid="cell-match">
                  <div className="truncate">{opp.match}</div>
                </TableCell>
                <TableCell>
                  <Badge variant="outline" className="font-semibold text-[10px] sm:text-xs whitespace-nowrap">
                    {opp.sport}
                  </Badge>
                </TableCell>
                <TableCell>
                  <div className="flex flex-wrap gap-1 sm:gap-1.5">
                    {opp.bookmakers.map((bm, idx) => (
                      <Badge 
                        key={idx} 
                        variant="secondary" 
                        className="text-[10px] sm:text-xs font-medium whitespace-nowrap"
                      >
                        {bm.name}
                      </Badge>
                    ))}
                  </div>
                </TableCell>
                <TableCell className="text-right">
                  <div className={cn(
                    "inline-flex items-center gap-1 sm:gap-1.5 px-2 sm:px-3 py-1 sm:py-1.5 rounded-lg font-bold tabular-nums text-sm sm:text-base whitespace-nowrap",
                    getProfitBgColor(opp.profit),
                    getProfitColor(opp.profit)
                  )} data-testid="cell-profit">
                    {opp.profit.toFixed(2)}%
                  </div>
                </TableCell>
                <TableCell className="text-right">
                  <span className="font-bold tabular-nums text-xs sm:text-sm whitespace-nowrap" data-testid="cell-stake">
                    ${totalStake.toFixed(2)}
                  </span>
                </TableCell>
                <TableCell className="text-right">
                  <span className={cn(
                    "font-bold tabular-nums text-xs sm:text-sm whitespace-nowrap",
                    getProfitColor(opp.profit)
                  )} data-testid="cell-returns">
                    ${guaranteedProfit.toFixed(2)}
                  </span>
                </TableCell>
                <TableCell className="text-right hidden sm:table-cell">
                  <span className="text-xs text-muted-foreground font-medium tabular-nums whitespace-nowrap" data-testid="cell-time">
                    {timeAgo(opp.timestamp)}
                  </span>
                </TableCell>
                <TableCell className="text-right">
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      onClick?.(opp);
                    }}
                    className="gap-1 sm:gap-1.5 font-semibold text-xs sm:text-sm"
                    data-testid="button-view-details"
                  >
                    <DollarSign className="h-3.5 w-3.5 sm:h-4 sm:w-4" />
                    <span className="hidden sm:inline">View</span>
                  </Button>
                </TableCell>
              </TableRow>
            );
          })}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}

client/src/components/StatsOverview.tsx.content:
import { Card } from "@/components/ui/card";
import { TrendingUp, DollarSign, Clock, Target, Sparkles, ArrowUpRight } from "lucide-react";
import { cn } from "@/lib/utils";

interface StatsOverviewProps {
  totalOpportunities: number;
  avgProfit: number;
  lastUpdated: string;
  isLoading?: boolean;
}

export function StatsOverview({ 
  totalOpportunities, 
  avgProfit, 
  lastUpdated,
  isLoading = false 
}: StatsOverviewProps) {
  const maxPotential = totalOpportunities * 100 * avgProfit / 100;

  const stats = [
    {
      label: "Total Opportunities",
      value: isLoading ? "--" : totalOpportunities.toString(),
      icon: Target,
      color: "text-primary",
      bgColor: "bg-primary/10",
      borderColor: "border-primary/20",
      change: totalOpportunities > 0 ? "Active" : "None",
      trend: "up" as const,
    },
    {
      label: "Avg Profit",
      value: isLoading ? "--" : `${avgProfit.toFixed(2)}%`,
      icon: TrendingUp,
      color: "text-success",
      bgColor: "bg-success/10",
      borderColor: "border-success/20",
      change: avgProfit > 1 ? "Good" : "Low",
      trend: "up" as const,
    },
    {
      label: "Max Potential",
      value: isLoading ? "--" : `$${maxPotential.toFixed(0)}`,
      icon: DollarSign,
      color: "text-warning",
      bgColor: "bg-warning/10",
      borderColor: "border-warning/20",
      change: `On $${(totalOpportunities * 100).toFixed(0)}`,
      trend: null,
    },
    {
      label: "Last Update",
      value: isLoading ? "--" : lastUpdated,
      icon: Clock,
      color: "text-muted-foreground",
      bgColor: "bg-muted/50",
      borderColor: "border-border/50",
      change: "Live data",
      trend: null,
    },
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4 lg:gap-6">
      {stats.map((stat, index) => (
        <Card 
          key={index} 
          className={cn(
            "relative overflow-hidden group hover-elevate transition-all duration-300",
            "border-2", 
            stat.borderColor
          )} 
          data-testid={`card-stat-${index}`}
        >
          {/* Background gradient */}
          <div className={cn(
            "absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300",
            "bg-gradient-to-br from-transparent to-black/5 dark:to-white/5"
          )} />

          <div className="relative p-4 sm:p-5 lg:p-6 space-y-3 sm:space-y-4">
            {/* Icon and Label */}
            <div className="flex items-center justify-between">
              <div className={cn(
                "p-2 sm:p-2.5 lg:p-3 rounded-lg sm:rounded-xl transition-all duration-300",
                stat.bgColor,
                "group-hover:scale-110"
              )}>
                <stat.icon className={cn("h-4 w-4 sm:h-5 sm:w-5", stat.color)} />
              </div>
              {stat.trend && (
                <ArrowUpRight className={cn("h-3.5 w-3.5 sm:h-4 sm:w-4", stat.color, "opacity-50")} />
              )}
            </div>

            {/* Label */}
            <div className="space-y-1">
              <p className="text-[10px] sm:text-xs text-muted-foreground uppercase tracking-wider font-bold">
                {stat.label}
              </p>

              {/* Value */}
              <p className={cn(
                "text-2xl sm:text-3xl font-bold tracking-tight tabular-nums",
                isLoading && "animate-pulse",
                stat.color
              )} data-testid={`text-stat-value-${index}`}>
                {stat.value}
              </p>

              {/* Change indicator */}
              <p className="text-[10px] sm:text-xs text-muted-foreground font-medium flex items-center gap-1">
                {stat.trend && <Sparkles className="h-2.5 w-2.5 sm:h-3 sm:w-3" />}
                {stat.change}
              </p>
            </div>
          </div>
        </Card>
      ))}
    </div>
  );
}
