import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { createOddsProvider, oddsCache } from "./odds-provider";
import { findAllArbitrageOpportunities } from "./arbitrage-engine";
import { 
  getOddsRequestSchema,
  type Sport,
  type GetOddsResponse,
  type HealthCheckResponse 
} from "@shared/schema";
import { z } from "zod";

// ============================================================================
// API ROUTES FOR ARBITRAGE SCANNER
// ============================================================================

export async function registerRoutes(app: Express): Promise<Server> {

  // ========================================
  // GET /api/odds - Fetch arbitrage opportunities
  // ========================================
  app.get("/api/odds", async (req, res) => {
    try {
      // Parse and validate query parameters
      const queryParams = {
        sports: req.query.sports 
          ? (typeof req.query.sports === 'string' ? req.query.sports.split(',') : req.query.sports)
          : undefined,
        minProfit: req.query.minProfit ? parseFloat(req.query.minProfit as string) : 0,
        bookmakers: req.query.bookmakers
          ? (typeof req.query.bookmakers === 'string' ? req.query.bookmakers.split(',') : req.query.bookmakers)
          : undefined,
      };

      // Validate with Zod
      const validated = getOddsRequestSchema.parse(queryParams);

      // Get settings to determine mock mode
      const settings = await storage.getSettings();
      const apiKey = process.env.ODDS_API_KEY || req.headers['x-api-key'] as string;

      // Create odds provider (mock or real)
      const provider = createOddsProvider(apiKey, settings.mockMode);

      console.log(`[API] Fetching odds using ${provider.getName()}`);

      // Default to 'upcoming' which returns games across all sports
      // This is always valid and doesn't require specific sport keys
      const sports = (validated.sports || ["upcoming"]) as Sport[];

      // Fetch odds from provider
      const oddsData = await provider.fetchOdds(sports);

      // Calculate arbitrage opportunities
      const opportunities = findAllArbitrageOpportunities(
        oddsData,
        validated.minProfit || 0
      );

      // Filter by bookmakers if specified
      let filteredOpportunities = opportunities;
      if (validated.bookmakers && validated.bookmakers.length > 0) {
        filteredOpportunities = opportunities.filter(opp => 
          opp.bookmakers.some(b => validated.bookmakers!.includes(b.name))
        );
      }

      // Cache opportunities in storage
      await storage.setCachedOpportunities(filteredOpportunities);

      const response: GetOddsResponse = {
        opportunities: filteredOpportunities,
        count: filteredOpportunities.length,
        cachedAt: new Date().toISOString(),
      };

      res.json(response);
    } catch (error) {
      console.error("[API] Error in /api/odds:", error);

      if (error instanceof z.ZodError) {
        return res.status(400).json({
          message: "Invalid request parameters",
          errors: error.errors,
        });
      }

      res.status(500).json({
        message: error instanceof Error ? error.message : "Internal server error",
      });
    }
  });

  // ========================================
  // GET /healthz - Health check (spec requirement)
  // ========================================
  app.get("/healthz", async (req, res) => {
    try {
      const cacheStats = oddsCache.getStats();

      const response: HealthCheckResponse = {
        status: "healthy",
        timestamp: new Date().toISOString(),
        services: {
          api: true,
          cache: cacheStats.activeEntries >= 0,
        },
      };

      res.json(response);
    } catch (error) {
      const response: HealthCheckResponse = {
        status: "unhealthy",
        timestamp: new Date().toISOString(),
        services: {
          api: false,
          cache: false,
        },
      };

      res.status(503).json(response);
    }
  });

  // ========================================
  // GET /api/healthz - Health check (alias)
  // ========================================
  app.get("/api/healthz", async (req, res) => {
    try {
      const cacheStats = oddsCache.getStats();

      const response: HealthCheckResponse = {
        status: "healthy",
        timestamp: new Date().toISOString(),
        services: {
          api: true,
          cache: cacheStats.activeEntries >= 0,
        },
      };

      res.json(response);
    } catch (error) {
      const response: HealthCheckResponse = {
        status: "unhealthy",
        timestamp: new Date().toISOString(),
        services: {
          api: false,
          cache: false,
        },
      };

      res.status(503).json(response);
    }
  });

  // ========================================
  // GET /api/settings - Get current settings
  // ========================================
  app.get("/api/settings", async (req, res) => {
    try {
      const settings = await storage.getSettings();
      res.json(settings);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Internal server error",
      });
    }
  });

  // ========================================
  // POST /api/settings - Update settings
  // ========================================
  app.post("/api/settings", async (req, res) => {
    try {
      const updates = req.body;
      const settings = await storage.updateSettings(updates);
      res.json(settings);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Internal server error",
      });
    }
  });

  // ========================================
  // GET /api/cache/stats - Cache statistics
  // ========================================
  app.get("/api/cache/stats", (req, res) => {
    try {
      const stats = oddsCache.getStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Internal server error",
      });
    }
  });

  // ========================================
  // POST /api/cache/clear - Clear cache
  // ========================================
  app.post("/api/cache/clear", (req, res) => {
    try {
      oddsCache.clear();
      res.json({ message: "Cache cleared successfully" });
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Internal server error",
      });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import type { ArbitrageOpportunity, Settings } from "@shared/schema";

// ============================================================================
// IN-MEMORY STORAGE INTERFACE
// ============================================================================
// For arbitrage scanner, we primarily rely on real-time calculations
// Storage is minimal - just for caching opportunities and settings

export interface IStorage {
  // Settings management
  getSettings(): Promise<Settings>;
  updateSettings(settings: Partial<Settings>): Promise<Settings>;

  // Opportunities cache (optional, mainly for performance)
  getCachedOpportunities(): Promise<ArbitrageOpportunity[]>;
  setCachedOpportunities(opportunities: ArbitrageOpportunity[]): Promise<void>;
}

export class MemStorage implements IStorage {
  private settings: Settings;
  private cachedOpportunities: ArbitrageOpportunity[];

  constructor() {
    this.settings = {
      mockMode: process.env.MOCK_ODDS === "true",
      cacheTimeout: 60,
    };
    this.cachedOpportunities = [];
  }

  async getSettings(): Promise<Settings> {
    return { ...this.settings };
  }

  async updateSettings(updates: Partial<Settings>): Promise<Settings> {
    this.settings = { ...this.settings, ...updates };
    return this.getSettings();
  }

  async getCachedOpportunities(): Promise<ArbitrageOpportunity[]> {
    return [...this.cachedOpportunities];
  }

  async setCachedOpportunities(opportunities: ArbitrageOpportunity[]): Promise<void> {
    this.cachedOpportunities = opportunities;
  }
}

export const storage = new MemStorage();

