client/src/components/FilterBar.tsx.content:
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { X } from "lucide-react";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";

const SPORTS = [
  { value: "all", label: "All Sports", icon: "sports" },
  { value: "soccer", label: "Soccer", icon: "sports_soccer" },
  { value: "basketball", label: "Basketball", icon: "sports_basketball" },
  { value: "tennis", label: "Tennis", icon: "sports_tennis" },
];

const BOOKMAKERS = [
  "Bet365",
  "DraftKings",
  "FanDuel",
  "BetMGM",
  "Caesars",
  "PointsBet",
];

interface FilterBarProps {
  selectedSport: string;
  selectedBookmakers: string[];
  minProfit: number;
  onSportChange: (sport: string) => void;
  onBookmakerToggle: (bookmaker: string) => void;
  onMinProfitChange: (profit: number) => void;
  onClearFilters: () => void;
}

export default function FilterBar({
  selectedSport,
  selectedBookmakers,
  minProfit,
  onSportChange,
  onBookmakerToggle,
  onMinProfitChange,
  onClearFilters,
}: FilterBarProps) {
  const hasFilters = selectedSport !== "all" || selectedBookmakers.length > 0 || minProfit > 0;

  return (
    <div className="sticky top-16 z-40 border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4">
        <div className="flex flex-col gap-4">
          <div className="flex flex-wrap items-center gap-3">
            <Select value={selectedSport} onValueChange={onSportChange}>
              <SelectTrigger className="w-[160px]" data-testid="select-sport">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {SPORTS.map((sport) => (
                  <SelectItem key={sport.value} value={sport.value}>
                    <div className="flex items-center gap-2">
                      <span className="material-icons text-base">{sport.icon}</span>
                      {sport.label}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <div className="flex flex-wrap gap-2">
              {BOOKMAKERS.map((bookmaker) => (
                <Badge
                  key={bookmaker}
                  variant={selectedBookmakers.includes(bookmaker) ? "default" : "outline"}
                  className="cursor-pointer hover-elevate"
                  onClick={() => onBookmakerToggle(bookmaker)}
                  data-testid={`badge-bookmaker-${bookmaker.toLowerCase()}`}
                >
                  {bookmaker}
                </Badge>
              ))}
            </div>

            {hasFilters && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onClearFilters}
                data-testid="button-clear-filters"
                className="ml-auto"
              >
                <X className="h-4 w-4 mr-1" />
                Clear
              </Button>
            )}
          </div>

          <div className="flex items-center gap-4">
            <span className="text-sm text-muted-foreground whitespace-nowrap">Min Profit:</span>
            <Slider
              value={[minProfit]}
              onValueChange={([value]) => onMinProfitChange(value)}
              max={10}
              step={0.1}
              className="w-full max-w-xs"
              data-testid="slider-min-profit"
            />
            <span className="text-sm font-mono font-medium min-w-[3rem]" data-testid="text-profit-value">
              {minProfit.toFixed(1)}%
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/SettingsDialog.tsx.content:
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Eye, EyeOff } from "lucide-react";

interface SettingsDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function SettingsDialog({ open, onOpenChange }: SettingsDialogProps) {
  const [apiKey, setApiKey] = useState(() => localStorage.getItem("oddsApiKey") || "");
  const [showApiKey, setShowApiKey] = useState(false);
  const [mockMode, setMockMode] = useState(() => localStorage.getItem("mockMode") === "true");

  const handleSave = async () => {
    // Save to localStorage
    localStorage.setItem("oddsApiKey", apiKey);
    localStorage.setItem("mockMode", mockMode.toString());

    // Save to backend
    try {
      await fetch("/api/settings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ mockMode }),
      });
      console.log("Settings saved:", { apiKey: apiKey ? "***" : "empty", mockMode });
    } catch (error) {
      console.error("Failed to save settings to backend:", error);
    }

    onOpenChange(false);

    // Force a refresh to apply new settings
    window.location.reload();
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]" data-testid="dialog-settings">
        <DialogHeader>
          <DialogTitle>Settings</DialogTitle>
          <DialogDescription>
            Configure your API settings and preferences
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 py-4">
          <div className="space-y-2">
            <Label htmlFor="api-key">The Odds API Key</Label>
            <div className="relative">
              <Input
                id="api-key"
                type={showApiKey ? "text" : "password"}
                placeholder="Enter your API key"
                value={apiKey}
                onChange={(e) => setApiKey(e.target.value)}
                className="pr-10"
                data-testid="input-api-key"
              />
              <Button
                type="button"
                variant="ghost"
                size="icon"
                className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                onClick={() => setShowApiKey(!showApiKey)}
                data-testid="button-toggle-api-key"
              >
                {showApiKey ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            <p className="text-xs text-muted-foreground">
              Get your API key from{" "}
              <a
                href="https://the-odds-api.com"
                target="_blank"
                rel="noopener noreferrer"
                className="text-primary hover:underline"
              >
                the-odds-api.com
              </a>
            </p>
          </div>

          <div className="flex items-center justify-between">
            <div className="space-y-0.5">
              <Label htmlFor="mock-mode">Mock Data Mode</Label>
              <p className="text-xs text-muted-foreground">
                Use simulated data for testing without API calls
              </p>
            </div>
            <Switch
              id="mock-mode"
              checked={mockMode}
              onCheckedChange={setMockMode}
              data-testid="switch-mock-mode"
            />
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={() => onOpenChange(false)} data-testid="button-cancel">
            Cancel
          </Button>
          <Button onClick={handleSave} data-testid="button-save">
            Save Changes
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/StatusIndicator.ts.content:
import { cn } from "@/lib/utils";

type StatusType = "connected" | "disconnected" | "cached";

interface StatusIndicatorProps {
  status: StatusType;
  className?: string;
}

export default function StatusIndicator({ status, className }: StatusIndicatorProps) {
  const statusConfig = {
    connected: {
      color: "bg-green-500",
      label: "Live",
      pulse: true,
    },
    disconnected: {
      color: "bg-red-500",
      label: "Offline",
      pulse: false,
    },
    cached: {
      color: "bg-yellow-500",
      label: "Cached",
      pulse: false,
    },
  };

  const config = statusConfig[status];

  return (
    <div className={cn("flex items-center gap-2", className)} data-testid={`status-${status}`}>
      <div className="relative">
        <div className={cn("h-2 w-2 rounded-full", config.color)} />
        {config.pulse && (
          <div className={cn("absolute inset-0 h-2 w-2 rounded-full animate-ping", config.color)} />
        )}
      </div>
      <span className="text-sm text-muted-foreground">{config.label}</span>
    </div>
  );
}

